// Action class
class Action {
    constructor(name, actionCost, spellLevel, tags, prerequisites, traditions, domain, cast, cost, frequency, trigger, requirements, range, area, targets, savingThrow, duration, description, criticalSuccess, success, failure, criticalFailure, moreDescription, heightenedLevel, heightenedText, extraTitle, extraText) {
        this.name = name;
        this.actionCost = actionCost;
        this.spellLevel = spellLevel;
        this.tags = tags;
        this.prerequisites = prerequisites;
        this.traditions = traditions;
        this.domain = domain;
        this.cast = cast;
        this.cost = cost;
        this.frequency = frequency;
        this.trigger = trigger;
        this.requirements = requirements;
        this.range = range;
        this.area = area;
        this.targets = targets;
        this.savingThrow = savingThrow;
        this.duration = duration;
        this.description = description;
        this.criticalSuccess = criticalSuccess;
        this.success = success;
        this.failure = failure;
        this.criticalFailure = criticalFailure;
        this.moreDescription = moreDescription;
        this.heightenedLevel = heightenedLevel;
        this.heightenedText = heightenedText;
        this.extraTitle = extraTitle;
        this.extraText = extraText;
    }
}

