const allegro = new Action("Allegro", 1, 7, ["UNCOMMON", "BARD", "CANTRIP", "COMPOSITION", "EMOTION", "ENCHANTMENT", "MENTAL"], "", [], "", "verbal", "", "", "", "", "30 feet", "", "1 ally", "", "1 round", "You perform rapidly, speeding up your ally. The ally becomes quickened and can use the additional action to Strike, Stride, or Step.", "", "", "", "", "", [], [], [], []);
