// changes number of turns by conditions
const applyConditions = () => {

    // resets chosen actionspplyConditions
    resetTurns();

    // sets number of turns by condition
    let turns = numberOfTurns();

    // displays number of turns
    if (turns > 0) {
        document.getElementById("turn1").style.display = "block";
        if (turns > 1) {
            document.getElementById("turn2").style.display = "block";
            if (turns > 2) {
                document.getElementById("turn3").style.display = "block";
                if (turns > 3) {
                    document.getElementById("turn4").style.display = "block";
                }
                else {
                    document.getElementById("turn4").style.display = "none";
                }
            }
            else {
                document.getElementById("turn3").style.display = "none";
            }
        }
        else {
            document.getElementById("turn2").style.display = "none";
        }
    }
    else {
        document.getElementById("turn1").style.display = "none";
    }
    switchGrid();

    // recalculates actions options
    filterActions();
}
document.getElementById("quickened").onclick = applyConditions;
document.getElementById("slowed1").onclick = () => {

    // can only be slowed 2 if slowed 1
    if (document.getElementById("slowed1").checked) {
        document.getElementById("slowed2").disabled = false;
    }
    else {
        document.getElementById("slowed2").checked = false;
        document.getElementById("slowed3").checked = false;
        document.getElementById("slowed2").disabled = true;
        document.getElementById("slowed3").disabled = true;        
    }

    applyConditions();
};
document.getElementById("slowed2").onclick = () => {

    // can only be slowed 3 if slowed 2
    if (document.getElementById("slowed2").checked) {
        document.getElementById("slowed3").disabled = false;
    }
    else {
        document.getElementById("slowed3").checked = false;
        document.getElementById("slowed3").disabled = true;        
    }
    
    applyConditions();
};
document.getElementById("slowed3").onclick = applyConditions;

// filter actions by filters
const filterActionsByFilters = () => {

    // reset actions
    actions = [noAction];

    // add back checked action arrays
    for (let i = 0; i < actionArrays.length; i++) {
        if (document.getElementById(actionArrayVarNames[i]).checked) {
            actionArrays[i].forEach(action => {
                if (!actions.includes(action)) {
                    actions.push(action);
                }
            });
        }
    }

    // resets actionNames
    actionNames = actions.map(action => {
        return action.name;
    });

    // resets chosen actions
    applyConditions();
};
filterActionsByFilters();
actionArrayVarNames.forEach(arrayName => {
    document.getElementById(arrayName).onclick = filterActionsByFilters;
});

// spellActions filter should reveal the spell filters
document.getElementById("spellActions").onclick = () => {
    filterActionsByFilters();
    if (document.getElementById("spellActions").checked) {
        document.getElementById("spellFilters").style.display = "block";
    }
    else {
        document.getElementById("spellFilters").style.display = "none";
    }
};

// filter spells by their attributes
const traditions = ["arcane", "divine", "occult", "primal"];
let tags = [];
spellActions.forEach(spell => {
    spell.tags.forEach(tag => {
        if (!tags.includes(tag) && tag != "UNCOMMON" && tag != "RARE") {
            tags.push(tag);
        }
    });
});
tags = ["UNCOMMON", "RARE"].concat(tags.sort());
const spellFilter = () => {

    // removes spells from actions
    actions = actions.filter(action => {
        return !spellActions.includes(action);
    });

    // adds back checked traditions and tags
    spellActions.forEach(spell => {
        traditions.forEach(tradition => {
            if (document.getElementById(tradition).checked && spell.traditions.includes(tradition) && !actions.includes(spell)) {
                tags.forEach(tag => {
                    if (document.getElementById(tag).checked && spell.tags.includes(tag)) {
                        actions.push(spell);
                    }
                });
            }
        });
    });

    // resets actionNames
    actionNames = actions.map(action => {
        return action.name;
    });
    // resets chosen actions
    applyConditions();
};

// filter spell actions by tradition
document.getElementById("spellFilters").innerHTML += "<h3>Traditions</h3>";
traditions.forEach(trad => {
    document.getElementById("spellFilters").innerHTML += `<div><input type="checkbox" name="${trad}" id="${trad}" checked>
    <label for="${trad}">${trad[0].toUpperCase() + trad.substring(1)}</label></div>`;
});

// filter spell actions by tag
document.getElementById("spellFilters").innerHTML += "<br/><h3>Tags</h3>";
tags.forEach(tag => {
    document.getElementById("spellFilters").innerHTML += `<div><input type="checkbox" name="${tag}" id="${tag}" checked>
    <label for="${tag}">${tag[0].toUpperCase() + tag.substring(1).toLowerCase()}</label></div>`;
});

// onclick for filtering spells
traditions.forEach(trad => {
    document.getElementById(trad).onclick = spellFilter;
});
tags.forEach(tag => {
    document.getElementById(tag).onclick = spellFilter;
});